INSERT INTO `posts` (`id`, `categoryId`, `image`, `productName`, `details`, `status`) VALUES
(286, 123, '481960166_1162571062544947_8286870260593327820_n-67ebc310a42cc.jpg', 'CLB Thanh Hóa giải quyết xong lệnh cấm từ FIFA', '<p>CLB Thanh H&oacute;a bất ngờ xuất hiện trong danh s&aacute;ch c&aacute;c CLB b&oacute;ng đ&aacute; bị cấm chuyển nhượng trong 3 kỳ li&ecirc;n tiếp tr&ecirc;n trang chủ FIFA. Nhưng rất nhanh, đội b&oacute;ng xứ Thanh đ&atilde; xử l&yacute; được vấn đề n&agrave;y.</p>\r\n\r\n<p>Tr&ecirc;n trang chủ ch&iacute;nh thức của LĐBĐ Thế giới (FIFA), CLB Thanh H&oacute;a bất ngờ xuất hiện trong danh s&aacute;ch c&aacute;c CLB b&oacute;ng đ&aacute; bị cấm chuyển nhượng trong 3 kỳ li&ecirc;n tiếp, bắt đầu từ cuối th&aacute;ng 3/2025.</p>\r\n\r\n<p>Điều n&agrave;y đồng nghĩa với việc đội b&oacute;ng xứ Thanh sẽ kh&ocirc;ng được k&yacute; hợp đồng với cầu thủ mới trong 3 kỳ chuyển nhượng li&ecirc;n tiếp, từ giai đoạn c&ograve;n lại của m&ugrave;a giải 2024/25 đến giữa m&ugrave;a giải 2026/27.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/levy/2025/04/01/fifa-ban-67ebdcf6a36fd.png\"><img alt=\"Thực hư việc CLB Thanh Hóa bất ngờ nhận lệnh cấm từ FIFA 613778\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/levy/2025/04/01/fifa-ban-67ebdcf6a36fd.png\" style=\"height:547px; width:1001px\" /></a></p>\r\n\r\n<p>Theo nhiều nguồn tin uy t&iacute;n, CLB Thanh H&oacute;a&nbsp;<strong>c&ograve;n một khoản tiền đền b&ugrave; chưa thanh to&aacute;n cho cựu trợ l&yacute; Svetislav Tanasijevic</strong>, dựa tr&ecirc;n quyết định từ FIFA. Được biết, LĐBĐ Thế giới sẵn s&agrave;ng gỡ lệnh cấm nếu c&aacute;c CLB sớm chấp h&agrave;nh &aacute;n phạt.</p>\r\n\r\n<p>V&agrave;&nbsp;<strong>ngay sau khi tiếp nhận th&ocirc;ng tin từ FIFA, đội b&oacute;ng xứ Thanh đ&atilde; nhanh ch&oacute;ng thực hiện tất to&aacute;n khoản tiền n&agrave;y cho cựu trợ l&yacute; người Serbia. Do đ&oacute;, lệnh cấm chuyển nhượng của LĐBĐ Thế giới đối với CLB Thanh H&oacute;a cũng sớm được gỡ bỏ.</strong></p>\r\n\r\n<p>Cần n&oacute;i th&ecirc;m, &ocirc;ng Svetislav Tanasijevic từng đảm nhiệm vai tr&ograve; trợ l&yacute; cho cựu HLV trưởng Petrovic khi nh&agrave; cầm qu&acirc;n đồng hương Serbia dẫn dắt CLB Thanh H&oacute;a trong 2 m&ugrave;a giải 2021-2022.</p>\r\n\r\n<p>Cựu trợ l&yacute; Tanasijevic c&ograve;n từng được bổ nhiệm l&agrave;m HLV trưởng tạm quyền của CLB Thanh H&oacute;a sau khi HLV Petrovic rời &quot;ghế n&oacute;ng&quot;. Tuy nhi&ecirc;n, do th&agrave;nh t&iacute;ch kh&ocirc;ng thực sự khởi sắc n&ecirc;n ban l&atilde;nh đạo CLB đ&atilde; thay thế bằng HLV Velizar Popov.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/ngachelsea/2025/04/01/192621151_2617263678577352_6034910084198182893_n-67ebc76e709f0.jpg\"><img alt=\"Thanh Hóa bất ngờ bị FIFA cấm chuyển nhượng 613751\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/ngachelsea/2025/04/01/192621151_2617263678577352_6034910084198182893_n-67ebc76e709f0.jpg\" style=\"height:1365px; width:2048px\" /></a></p>\r\n\r\n<p>Thanh H&oacute;a đ&atilde; thanh to&aacute;n tiền đền b&ugrave; cho &ocirc;ng Tanasijevic, chờ FIFA gỡ lệnh cấm (Ảnh: CLB)</p>\r\n\r\n<p>Đến cuối năm 2022, &ocirc;ng Svetislav Tanasijevic được t&iacute;n nhiệm bổ nhiệm l&agrave;m Gi&aacute;m đốc Trung t&acirc;m đ&agrave;o tạo trẻ của CLB Thanh H&oacute;a nhờ kinh nghiệm d&agrave;y dặn sau nhiều năm tham gia c&ocirc;ng t&aacute;c đ&agrave;o đạo trẻ ở một số nước Ch&acirc;u &Aacute; v&agrave; Ch&acirc;u Phi.&nbsp;</p>\r\n\r\n<p>M&ugrave;a giải 2023 kh&eacute;p lại cũng l&agrave; l&uacute;c hợp đồng giữa HLV Tanasijevic v&agrave; CLB Thanh H&oacute;a hết hạn. Đội b&oacute;ng xứ Thanh v&agrave; chiến lược gia người Serbia n&agrave;y đ&atilde; thống nhất chia tay sau 3 năm gắn b&oacute;.&nbsp;</p>\r\n\r\n<p>Về ph&iacute;a CLB Thanh H&oacute;a, đội b&oacute;ng n&agrave;y cũng vừa chia tay HLV Popov c&aacute;ch đ&acirc;y 1 th&aacute;ng v&agrave; bổ nhiệm chiến lược gia người Croatia - Tomislav Steinbruckner v&agrave;o vị tr&iacute; thuyền trưởng đội b&oacute;ng xứ Thanh.poiu</p>\r\n', 'hot'),
(287, 123, '488589108_702589972721713_14216780798181442_n-min-67ebdf5cc7262.jpg', 'HLV Polking, Quang Hải tự tin quyết đấu đội bóng Indonesia', '<p>HLV Mano Polking v&agrave; đội trưởng Quang Hải c&oacute; những ph&aacute;t biểu đ&aacute;ng ch&uacute; &yacute; trước b&aacute;n kết lượt đi CAHN l&agrave;m kh&aacute;ch của PSM Makassar tại C&uacute;p C1 Đ&ocirc;ng Nam &Aacute;.</p>\r\n\r\n<p>Ng&agrave;y mai (2/4), CLB CAHN sẽ bước v&agrave;o trận đấu b&aacute;n kết lượt đi giải v&ocirc; địch c&aacute;c CLB Đ&ocirc;ng Nam &Aacute; 2024/25 (C&uacute;p C1 Đ&ocirc;ng Nam &Aacute;) với trận đấu l&agrave;m kh&aacute;ch của PSM Makassar (Indonesia).</p>\r\n\r\n<p>Trước thềm trận đấu, HLV trưởng Mano Polking v&agrave; đội trưởng tiền vệ Nguyễn Quang Hải đ&atilde; tham dự họp b&aacute;o gặp gỡ b&aacute;o ch&iacute; truyền th&ocirc;ng v&agrave;o ng&agrave;y 1/4.</p>\r\n\r\n<p>Theo đ&oacute;, HLV Polking đ&aacute;nh gi&aacute;: &quot;<em>Tới thời điểm hiện tại, C&uacute;p C1 Đ&ocirc;ng Nam &Aacute; 2024/25 đang diễn ra su&ocirc;n sẻ với CLB CAHN v&agrave; t&ocirc;i rất vui khi c&ugrave;ng to&agrave;n đội g&oacute;p mặt tại v&ograve;ng b&aacute;n kết.</em></p>\r\n\r\n<p><em><strong>Trận lượt đi tại Indonesia sẽ l&agrave; trận đấu kh&oacute; khăn, nhưng ch&uacute;ng t&ocirc;i đ&atilde; chuẩn bị kỹ lưỡng.&nbsp;</strong>CLB CAHN cũng đ&atilde; di chuyển tới Makassar từ sớm để th&iacute;ch nghi với điều kiện kh&iacute; hậu.<strong>&nbsp;Hy vọng ch&uacute;ng t&ocirc;i sẽ c&oacute; kết quả tốt ở trận đấu ng&agrave;y mai</strong></em>&quot;.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/ngachelsea/2025/04/01/1743510522276_compressed-67ebe09158c2a.jpg\"><img alt=\"HLV Polking 613789\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/ngachelsea/2025/04/01/1743510522276_compressed-67ebe09158c2a.jpg\" style=\"height:100%; width:100%\" /></a></p>\r\n\r\n<p>HLV Polking quyết t&acirc;m hướng tới b&aacute;n kết lượt đi với PSM Makassar (Ảnh: CLB)</p>\r\n\r\n<p>Đ&aacute;nh gi&aacute; về đối thủ cũng như nhận định về trận đấu ng&agrave;y mai, &ocirc;ng tiếp tục n&oacute;i: &quot;<em>PSM Makassar l&agrave; đối thủ rất mạnh. Họ đ&atilde; l&ecirc;n ng&ocirc;i giải VĐQG 2 năm trước. Hiện tại, đội b&oacute;ng n&agrave;y đ&atilde; thay đổi với điểm mạnh l&agrave; những t&igrave;nh huống cố định.<strong>&nbsp;Nhưng CLB CAHN tự tin v&agrave; sẵn s&agrave;ng với những chiến lược m&agrave; c&aacute;c th&agrave;nh vi&ecirc;n trong BHL đ&atilde; chuẩn bị.</strong></em></p>\r\n\r\n<p><em>Ng&agrave;y mai, sẽ c&oacute; rất đ&ocirc;ng c&aacute;c cổ động vi&ecirc;n tới s&acirc;n theo d&otilde;i trận đấu. Hy vọng cả hai đội sẽ cống hiến cho kh&aacute;n giả một trận đấu hấp dẫn, kh&ocirc;ng chỉ d&agrave;nh cho c&aacute;c cổ động vi&ecirc;n Makassar n&oacute;i ri&ecirc;ng, m&agrave; c&ograve;n d&agrave;nh cho người h&acirc;m mộ b&oacute;ng đ&aacute; Đ&ocirc;ng Nam &Aacute; n&oacute;i chung</em>&quot;.</p>\r\n\r\n<p>Về phần m&igrave;nh, đội trưởng Quang Hải chia sẻ: &quot;<em>CLB CAHN đ&atilde; trải qua h&agrave;nh tr&igrave;nh di chuyển rất d&agrave;i để c&oacute; mặt tại đ&acirc;y.&nbsp;<strong>To&agrave;n đội đ&atilde; c&oacute; sự chuẩn bị tốt v&agrave; đ&atilde; nghi&ecirc;n cứu tổng thể đối thủ để hướng tới m&agrave;n tr&igrave;nh diễn tốt nhất ở trận đấu ng&agrave;y mai</strong></em>&quot;.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/ngachelsea/2025/04/01/1743510541423_compressed-67ebe1e08aa99.jpg\"><img alt=\"HLV Polking 613794\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/ngachelsea/2025/04/01/1743510541423_compressed-67ebe1e08aa99.jpg\" style=\"height:100%; width:100%\" /></a></p>\r\n\r\n<p>Quang Hải khẳng định CLB CAHN đ&atilde; c&oacute; sự chuẩn bị tốt (Ảnh: CLB)</p>\r\n\r\n<p>Cũng trong ng&agrave;y 2/4, cặp b&aacute;n kết c&ograve;n lại của C&uacute;p C1 Đ&ocirc;ng Nam &Aacute; 2024/25 giữa hai đại diện Th&aacute;i Lan - Buriram United với BG Pathum United.</p>\r\n\r\n<p>Theo lịch thi đấu, thầy tr&ograve; HLV Mano Polking sẽ đ&oacute;n tiếp PSM Makassar ở trận b&aacute;n kết lượt về, diễn ra v&agrave;o ng&agrave;y 30/4 tr&ecirc;n SVĐ H&agrave;ng Đẫy.</p>\r\n', 'hot'),
(288, 123, 'nghi-da-o-al-nassr-ronaldo-se-gia-nhap-ga-khong-lo-the-gioi-350774-d-67eb69d36217f.jpg', 'Chưa rời Al Nassr, Ronaldo bất ngờ được \'gã khổng lồ\' chọn chiêu mộ', '<p>Xuất hiện một CLB h&agrave;ng đầu muốn k&yacute; hợp đồng với Cristiano Ronaldo để thi đấu ở Club World Cup.</p>\r\n\r\n<p><a href=\"https://thethao247.vn/461-club-world-cup-bo-xa-champions-league-o-mot-dieu-d361096.html\" target=\"_blank\">Giải FIFA Club World Cup 2025&nbsp;</a>lần đầu ti&ecirc;n được tổ chức tại Mỹ đang thu h&uacute;t sự ch&uacute; &yacute; to&agrave;n cầu, v&agrave; Cristiano Ronaldo một lần nữa trở th&agrave;nh t&acirc;m điểm. Theo nhiều nguồn tin từ Fichajes v&agrave; Trung Đ&ocirc;ng, Al Hilal - đội b&oacute;ng gi&agrave;u th&agrave;nh t&iacute;ch nhất ch&acirc;u &Aacute; đang r&aacute;o riết theo đuổi si&ecirc;u sao người Bồ Đ&agrave;o Nha với tham vọng biến anh th&agrave;nh gương mặt đại diện cho chiến dịch to&agrave;n cầu sắp tới.</p>\r\n\r\n<p>D&ugrave; vẫn c&ograve;n hợp đồng với Al Nassr đến giữa năm 2025, Ronaldo đang được Al Hilal nhắm đến như mảnh gh&eacute;p ho&agrave;n hảo để thay thế Neymar, người vừa chia tay đội b&oacute;ng do chấn thương dai dẳng. Ban l&atilde;nh đạo Al Hilal tin rằng, với đẳng cấp v&agrave; kinh nghiệm đỉnh cao, CR7 kh&ocirc;ng chỉ n&acirc;ng tầm đội h&igrave;nh m&agrave; c&ograve;n g&oacute;p phần khẳng định vị thế thống trị của họ tại ch&acirc;u &Aacute; v&agrave; xa hơn nữa l&agrave; đấu trường thế giới.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/camhm/2025/04/01/fjcfgruxoamd1lv-67eb6b8e5a636.jpg\"><img alt=\"Chưa rời Al Nassr, Ronaldo bất ngờ được \'gã khổng lồ\' chọn chiêu mộ 613519\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/camhm/2025/04/01/fjcfgruxoamd1lv-67eb6b8e5a636.jpg\" style=\"height:1536px; width:1536px\" /></a></p>\r\n\r\n<p>Al Hilal nhắm chi&ecirc;u mộ Ronaldo (Ảnh: Crypto).</p>\r\n\r\n<p>Thực tế, Al Hilal từng cố gắng chi&ecirc;u mộ Ronaldo từ năm 2022, nhưng r&agrave;o cản ph&aacute;p l&yacute; từ Trung t&acirc;m Trọng t&agrave;i Thể thao khiến thương vụ kh&ocirc;ng thể th&agrave;nh hiện thực. Giờ đ&acirc;y, với c&aacute;nh cửa đang dần mở ra, v&agrave; Ronaldo vẫn duy tr&igrave; phong độ ấn tượng khi đ&atilde; ghi hơn 20 b&agrave;n m&ugrave;a n&agrave;y ở tuổi 40, khả năng về một bản hợp đồng bom tấn l&agrave; điều ho&agrave;n to&agrave;n c&oacute; thể xảy ra.</p>\r\n\r\n<p>Viễn cảnh Ronaldo kho&aacute;c &aacute;o xanh của Al Hilal đối đầu Real Madrid tại Club World Cup đang khiến giới mộ điệu h&aacute;o hức. Sự xuất hiện của một trong những cầu thủ vĩ đại nhất lịch sử chắc chắn sẽ l&agrave;m tăng đ&aacute;ng kể sức h&uacute;t cho giải đấu tại Mỹ, đồng thời khẳng định sự nghi&ecirc;m t&uacute;c của<a href=\"https://thethao247.vn/livescores/a-rap-xe-ut/saudi-professional-league/\" target=\"_blank\">&nbsp;c&aacute;c CLB Ả Rập&nbsp;</a>trong cuộc chơi b&oacute;ng đ&aacute; to&agrave;n cầu.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/camhm/2025/04/01/323092-67eb6bc773058.jpg\"><img alt=\"Chưa rời Al Nassr, Ronaldo bất ngờ được \'gã khổng lồ\' chọn chiêu mộ 613520\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/camhm/2025/04/01/323092-67eb6bc773058.jpg\" style=\"height:1500px; width:2073px\" /></a></p>\r\n\r\n<p>Ronaldo vẫn đ&aacute;p ứng được y&ecirc;u cầu về chuy&ecirc;n m&ocirc;n ở tuổi 40 (Ảnh: Getty).</p>\r\n\r\n<p>D&ugrave; chưa c&oacute; đề nghị ch&iacute;nh thức, nguồn tin th&acirc;n cận cho biết c&aacute;c cuộc đ&agrave;m ph&aacute;n hậu trường đang diễn ra v&agrave; Al Hilal dường như sẵn s&agrave;ng l&agrave;m mọi c&aacute;ch để đưa Ronaldo về s&acirc;n Prince Faisal bin Fahd, biến anh th&agrave;nh ng&ocirc;i sao s&aacute;ng nhất trong dự &aacute;n thể thao đầy tham vọng của họ.</p>\r\n', 'hot'),
(289, 123, 'dlbeatsnoopcom-final-yt6k1icbp3-67e7b7e6d2f40.jpg', 'Liverpool khao khát sở hữu trò cưng của Ancelotti tại Real', '<p>Liverpool muốn sở hữu tiền đạo đang đạt phong độ cao tại Real Madrid, trong khi đ&oacute;, The Kops cũng đang nhắm đến một tiền vệ của Barca.</p>\r\n\r\n<p>Nội dung ch&iacute;nhẨn / Hiện</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://thethao247.vn/395-liverpool-khao-khat-so-huu-tro-cung-cua-ancelotti-tai-real-d361408.html#mcetoc_0\">Liverpool nhắm tới Rodrygo của Real Madrid</a></li>\r\n	<li><a href=\"https://thethao247.vn/395-liverpool-khao-khat-so-huu-tro-cung-cua-ancelotti-tai-real-d361408.html#mcetoc_1\">Liverpool sẵn s&agrave;ng chi đậm để sở hữu Frenkie de Jong</a></li>\r\n</ul>\r\n\r\n<h2>Liverpool nhắm tới Rodrygo của Real Madrid</h2>\r\n\r\n<p>Theo Fichajes, Liverpool đang nhắm đến Rodrygo, người được xem l&agrave;&nbsp;<a href=\"https://m.thethao247.vn/395-bo-qua-mbappe-hay-vinicius-ancelotti-chi-uu-tien-duy-nhat-mot-ngoi-sao-tai-real-d339737.html\" target=\"_blank\">nh&acirc;n tố kh&ocirc;ng thể thiếu tr&ecirc;n h&agrave;ng c&ocirc;ng của Real Madrid dưới thời HLV Carlo Ancelotti</a>, như một phương &aacute;n thay thế tiềm năng cho Mohamed Salah.</p>\r\n\r\n<p>Tiền đạo người Brazil đang c&oacute; phong độ ấn tượng tại Real Madrid, nhưng tương lai của anh vẫn chưa r&otilde; r&agrave;ng trước kỳ chuyển nhượng m&ugrave;a h&egrave;. M&ugrave;a n&agrave;y, ch&acirc;n s&uacute;t 24 tuổi đ&atilde; ghi 13 b&agrave;n v&agrave; đ&oacute;ng g&oacute;p t&aacute;m pha kiến tạo sau 41 trận tr&ecirc;n mọi đấu trường. Với sự xuất hiện của Endrick trong đội h&igrave;nh, Rodrygo c&oacute; thể c&acirc;n nhắc ra đi để t&igrave;m kiếm cơ hội ra s&acirc;n thường xuy&ecirc;n hơn.</p>\r\n\r\n<p>Liverpool đang theo d&otilde;i t&igrave;nh h&igrave;nh s&aacute;t sao, nhất l&agrave; khi hợp đồng của Mohamed Salah sẽ hết hạn trong v&agrave;i th&aacute;ng tới. D&ugrave; đội chủ s&acirc;n Anfield đ&atilde; nỗ lực gia hạn, nhưng ng&ocirc;i sao người Ai Cập vẫn chưa đồng &yacute; k&yacute; hợp đồng mới.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/tranvutung/2025/03/29/dlbeatsnoopcom-final-3dvhopyieu-67e7b737c2131.jpg\"><img alt=\"Liverpool khao khát sở hữu trò cưng của Ancelotti tại Real 612378\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/tranvutung/2025/03/29/dlbeatsnoopcom-final-3dvhopyieu-67e7b737c2131.jpg\" style=\"height:1305px; width:2048px\" /></a></p>\r\n\r\n<p>Rodrygo được xem l&agrave; sự lựa chọn l&yacute; tưởng để thay thế Mohamed Salah tại Liverpool. Ảnh: Getty</p>\r\n\r\n<p>Nếu Salah ra đi v&agrave;o cuối m&ugrave;a, Liverpool sẽ t&igrave;m c&aacute;ch chi&ecirc;u mộ Rodrygo để thay thế. Tuy nhi&ecirc;n, tiền đạo người Brazil vẫn c&ograve;n hợp đồng với Real Madrid đến năm 2028, v&agrave; đội b&oacute;ng Ho&agrave;ng gia c&oacute; thể kh&ocirc;ng muốn để cầu thủ người Brazil rời đi.</p>\r\n\r\n<h2>Liverpool sẵn s&agrave;ng chi đậm để sở hữu Frenkie de Jong</h2>\r\n\r\n<p>Theo Fichajes, Chelsea v&agrave; Liverpool sẵn s&agrave;ng bỏ ra mức ph&iacute; khổng lồ để chi&ecirc;u mộ Frenkie de Jong từ Barcelona. Tiền vệ người H&agrave; Lan đang thu h&uacute;t sự quan t&acirc;m từ nhiều đội b&oacute;ng h&agrave;ng đầu Premier League nhờ phong độ ấn tượng.</p>\r\n\r\n<p>Ở tuổi 27, De Jong được đ&aacute;nh gi&aacute; cao nhờ khả năng đọc trận đấu, chuyền b&oacute;ng ch&iacute;nh x&aacute;c, kiểm so&aacute;t b&oacute;ng kh&eacute;o l&eacute;o, đi b&oacute;ng mượt m&agrave; v&agrave; sự linh hoạt trong lối chơi. Cầu thủ người H&agrave; Lan đ&atilde; ghi 19 b&agrave;n v&agrave; c&oacute; 22 pha kiến tạo sau 243 trận cho đội b&oacute;ng xứ Catalan tr&ecirc;n mọi đấu trường.</p>\r\n\r\n<p><a href=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/tranvutung/2025/03/29/dlbeatsnoopcom-final-ydpf8p2sge-67e7b7634fa9a.jpg\"><img alt=\"Liverpool khao khát sở hữu trò cưng của Ancelotti tại Real 612379\" src=\"https://cdn-img.thethao247.vn/origin_842x0/storage/files/tranvutung/2025/03/29/dlbeatsnoopcom-final-ydpf8p2sge-67e7b7634fa9a.jpg\" style=\"height:1403px; width:2048px\" /></a></p>\r\n\r\n<p>Liverpool v&agrave; Chelsea theo đuổi chữ k&yacute; của Frenkie de Jong. Ảnh: Getty</p>\r\n\r\n<p>D&ugrave; gặp nhiều chấn thương mắt c&aacute; trong hai năm qua khiến phong độ bị ảnh hưởng, De Jong đ&atilde; trở lại mạnh mẽ từ đầu năm nay, g&oacute;p c&ocirc;ng lớn v&agrave;o th&agrave;nh c&ocirc;ng của Barcelona ở đấu trường quốc nội lẫn ch&acirc;u &Acirc;u.</p>\r\n\r\n<p>M&agrave;n tr&igrave;nh diễn của cựu sao Ajax khiến Liverpool v&agrave; Chelsea đặc biệt quan t&acirc;m, khi cả hai đội đều muốn bổ sung một tiền vệ c&oacute; phong c&aacute;ch như anh trong kỳ chuyển nhượng m&ugrave;a h&egrave;. Cả hai CLB sẵn s&agrave;ng chi khoảng 65-70 triệu Euro để sở hữu chữ k&yacute; của De Jong.</p>\r\n', 'new'),
(290, 115, 'IMG_6727.jpg', 'VTV Bình Điền Long An đánh bại Binh chủng Thông tin 3-1', '<p>Ng&agrave;y 31.3,&nbsp;<a href=\"https://laodong.vn/the-thao/lich-phat-song-bong-chuyen-vtv-binh-dien-long-an-vs-binh-chung-thong-tin-1484486.ldo\" target=\"_blank\">VTV B&igrave;nh Điền Long An</a>&nbsp;đ&aacute;nh bại Binh chủng Th&ocirc;ng tin - TCT Đ&ocirc;ng Bắc với tỉ số 3-1 ở trận cuối giai đoạn 1 giải b&oacute;ng chuyền v&ocirc; địch quốc gia 2025.</p>\r\n\r\n<p><strong>Set 4:&nbsp;</strong>VTV B&igrave;nh Điền Long An 25-18 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Thanh Th&uacute;y đ&aacute;nh b&oacute;ng đập chắn ra ngo&agrave;i. VTV B&igrave;nh Điền Long An thắng set 4, qua đ&oacute; đ&aacute;nh bại Binh chủng Th&ocirc;ng tin với tỉ số 3-1 chung cuộc.</p>\r\n\r\n<p><strong>Set 4:</strong>&nbsp;VTV B&igrave;nh Điền Long An 20-12 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Như Anh ghi 2 điểm cho VTV B&igrave;nh Điền Long An. C&aacute;ch biệt đ&atilde; l&agrave; 8 điểm.</p>\r\n\r\n<p><strong>Set 4:&nbsp;</strong>VTV B&igrave;nh Điền Long An 14-9 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Thanh Th&uacute;y v&agrave; Natalia Lijewska ghi hai điểm li&ecirc;n tiếp cho VTV B&igrave;nh Điền Long An.</p>\r\n\r\n<p><strong>Set 4:&nbsp;</strong>VTV B&igrave;nh Điền Long An 7-10 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Như Anh chớp thời cơ đập b&oacute;ng cắm s&agrave;n, duy tr&igrave; c&aacute;ch biệt kh&aacute; an to&agrave;n cho VTV B&igrave;nh Điền Long An.</p>\r\n\r\n<p><strong>Set 4</strong>: VTV B&igrave;nh Điền Long An 6-5 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>VTV B&igrave;nh Điền Long An challenge thất bại khi Thanh Th&uacute;y đập b&oacute;ng ra ngo&agrave;i kh&ocirc;ng chạm chắn.</p>\r\n\r\n<p><strong>Set 4:</strong>&nbsp;VTV B&igrave;nh Điền Long An 2-2 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Mitrovic tranh chấp tr&ecirc;n lưới th&agrave;nh c&ocirc;ng, gỡ ho&agrave; cho Binh chủng Th&ocirc;ng tin.</p>\r\n\r\n<p><strong>18h50:</strong>&nbsp;Set 4 bắt đầu<strong>.</strong></p>\r\n\r\n<p><img alt=\"VTV Bình Điền Long An chơi áp đảo đối thủ. Ảnh: Chi Trần \" src=\"https://media-cdn-v2.laodong.vn/storage/newsportal/2025/3/31/1484743/Bong-Chuyen-2.jpeg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>VTV B&igrave;nh Điền Long An chơi &aacute;p đảo đối thủ. Ảnh: Chi Trần</p>\r\n\r\n<p><br />\r\n<strong>Set 3:</strong>&nbsp;VTV B&igrave;nh Điền Long An 25-22 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Thanh Th&uacute;y dứt điểm dọc bi&ecirc;n ấn định chiến thắng trong set 3 cho VTV B&igrave;nh Điền Long An.</p>\r\n\r\n<p><strong>Set 3:</strong>&nbsp;VTV B&igrave;nh Điền Long An 19-20 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Binh chủng Th&ocirc;ng tin c&oacute; điểm số thứ 20! Set đấu đang tiến tới những điểm số quyết định.</p>\r\n\r\n<p><strong>Set 3:&nbsp;</strong>VTV B&igrave;nh Điền Long An 17-17 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Kiều Trinh vừa gi&uacute;p cho Binh chủng Th&ocirc;ng tin qu&acirc;n b&igrave;nh được tỉ số. Hai đội đều chưa tạo ra được c&aacute;ch biệt an to&agrave;n so với đối phương.</p>\r\n\r\n<p><strong>Set 3:&nbsp;</strong>VTV B&igrave;nh Điền Long An 13-13 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Tỉ số được qu&acirc;n b&igrave;nh cho VTV B&igrave;nh Điền Long An!!! Hai đội đang b&aacute;m đuổi rất s&aacute;t ở set 3.</p>\r\n\r\n<p><strong>Set 3:&nbsp;</strong>VTV B&igrave;nh Điền Long An 10-12 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Đường chuyền kh&ocirc;ng tốt khiến Nguyễn Thị Phương d&ugrave; đ&atilde; sửa lưng nhưng kh&ocirc;ng thể đ&aacute;nh b&oacute;ng th&agrave;nh c&ocirc;ng. Điểm số thứ 10 d&agrave;nh cho VTV B&igrave;nh Điền Long An.</p>\r\n\r\n<p><strong>Set 3:</strong>&nbsp;VTV B&igrave;nh Điền Long An 7-10 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Binh chủng Th&ocirc;ng tin vừa c&oacute; điểm số thứ 10. Khoảng c&aacute;ch đang l&agrave;m 3 điểm d&agrave;nh cho đội b&oacute;ng &aacute;o l&iacute;nh.</p>\r\n\r\n<p><strong>Set 3:</strong>&nbsp;VTV B&igrave;nh Điền Long An 4-7 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Ngoại binh Natalia ph&aacute;t b&oacute;ng ra ngo&agrave;i. Binh chủng Th&ocirc;ng tin vẫn đang c&oacute; lợi thế về điểm số.</p>\r\n\r\n<p><strong>Set 3:</strong>&nbsp;VTV B&igrave;nh Điền Long An 1-2 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Phạm Thị Hiền đ&aacute;nh b&oacute;ng một ch&acirc;n sau đầu gi&uacute;p Binh chủng Th&ocirc;ng tin vượt l&ecirc;n dẫn trước ở đầu set 3.</p>\r\n\r\n<p><strong>18h22:&nbsp;</strong>Set 3 bắt đầu.</p>\r\n\r\n<p><img alt=\"Binh chủng Thông tin gỡ hòa ở set 2. Ảnh: Nguyên Vũ\" src=\"https://media-cdn-v2.laodong.vn/storage/newsportal/2025/3/31/1484743/1384Cfbe0654b60aef45.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Binh chủng Th&ocirc;ng tin gỡ h&ograve;a ở set 2. Ảnh: Chi Trần</p>\r\n\r\n<p><strong>Set 2:</strong>&nbsp;VTV B&igrave;nh Điền Long An 26-28 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Mitrovic ghi điểm ace, gi&uacute;p Binh chủng Th&ocirc;ng tin gi&agrave;nh chiến thắng nghẹt thở.</p>\r\n\r\n<p><strong>Set 2:&nbsp;</strong>VTV B&igrave;nh Điền Long An 25-25 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Mitrovic đập b&oacute;ng bạt chắn ra ngo&agrave;i.</p>\r\n\r\n<p><strong>Set 2:&nbsp;</strong>VTV B&igrave;nh Điền Long An 23-22 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Mitrovic đỡ b&oacute;ng lỗi, điểm ace gi&uacute;p VTV B&igrave;nh Điền Long An vượt l&ecirc;n ở thời điểm quan trọng.</p>\r\n\r\n<p><strong>Set 2:</strong>&nbsp;VTV B&igrave;nh Điền Long An 20-20 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Kiều Trinh dứt điểm ra ngo&agrave;i v&agrave; VTV B&igrave;nh Điền Long An đ&atilde; gỡ ho&agrave;.</p>\r\n\r\n<p><strong>Set 2:</strong>&nbsp;VTV B&igrave;nh Điền Long An 15-17 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Natalia cắt chuỗi l&ecirc;n điểm của Binh chủng Th&ocirc;ng tin. Tinh thần của đội b&oacute;ng &aacute;o l&iacute;nh đang l&ecirc;n cao.</p>\r\n\r\n<p><strong>Set 2:&nbsp;</strong>VTV B&igrave;nh Điền Long An 11-13 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Nguyễn Thị Phương k&eacute;o d&agrave;i chuỗi l&ecirc;n điểm cho Binh chủng Th&ocirc;ng tin.</p>\r\n\r\n<p><img alt=\"Nguyễn Phương đang thi đấu nỗ lực. Ảnh: Nguyên Vũ\" src=\"https://media-cdn-v2.laodong.vn/storage/newsportal/2025/3/31/1484743/Ce82424ddfa76ff936b6.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Nguyễn Phương đang thi đấu nỗ lực. Ảnh: Chi Trần</p>\r\n\r\n<p><strong>Set 2:&nbsp;</strong>VTV B&igrave;nh Điền Long An 10-8 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Thanh Th&uacute;y dứt điểm từ h&agrave;ng sau gi&uacute;p VTV B&igrave;nh Điền Long An kh&ocirc;i phục thế dẫn trước.</p>\r\n\r\n<p><strong>Set 2:&nbsp;</strong>VTV B&igrave;nh Điền Long An 6-7 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Mitrovic ghi điểm gi&uacute;p Binh chủng Th&ocirc;ng tin vượt l&ecirc;n.</p>\r\n\r\n<p><strong>Set 2:&nbsp;</strong>VTV B&igrave;nh Điền Long An 3-3 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Nguyễn Thị Phương đập b&oacute;ng cắm s&agrave;n uy lực.</p>\r\n\r\n<p><strong>17h50:&nbsp;</strong>Set 2 bắt đầu.</p>\r\n\r\n<p><img alt=\"Ngoại binh VTV Bình Điền Long An chơi nổi bật. Ảnh: Chi Trần\" src=\"https://media-cdn-v2.laodong.vn/storage/newsportal/2025/3/31/1484743/IMG_6726.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Ngoại binh VTV B&igrave;nh Điền Long An chơi nổi bật. Ảnh: Chi Trần</p>\r\n\r\n<p><br />\r\n<strong>Set 1:&nbsp;</strong>VTV B&igrave;nh Điền Long An 25-12 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Như Anh đập b&oacute;ng dọc bi&ecirc;n, kh&eacute;p lại set đấu dễ d&agrave;ng cho VTV B&igrave;nh Điền Long An.</p>\r\n\r\n<p><strong>Set 1:&nbsp;</strong>VTV B&igrave;nh Điền Long An 21-9 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Thanh Th&uacute;y đỡ bước một lỗi. Nhưng l&uacute;c n&agrave;y c&aacute;ch biệt đ&atilde; l&agrave; qu&aacute; lớn để san lấp.</p>\r\n\r\n<p><strong>Set 1:&nbsp;</strong>VTV B&igrave;nh Điền Long An 17-7 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Sau khi VTV B&igrave;nh Điền Long An ghi 10 điểm li&ecirc;n tiếp, Binh chủng Th&ocirc;ng tin xin hội &yacute; lần thứ hai.</p>\r\n\r\n<p><img alt=\"Long An có chuỗi lên điểm ấn tượng. Ảnh: Chi Trần\" src=\"https://media-cdn-v2.laodong.vn/storage/newsportal/2025/3/31/1484743/IMG_6727.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Long An c&oacute; chuỗi l&ecirc;n điểm ấn tượng. Ảnh: Chi Trần</p>\r\n\r\n<p><strong>Set 1:&nbsp;</strong>VTV B&igrave;nh Điền Long An 14-7 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>VTV B&igrave;nh Điền Long An ghi 7 điểm li&ecirc;n tiếp. Binh chủng Th&ocirc;ng tin đang chịu sức &eacute;p lớn.</p>\r\n\r\n<p><strong>Set 1:&nbsp;</strong>VTV B&igrave;nh Điền Long An 7-6 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Nguyễn Thị Phương đập b&oacute;ng về cuối s&acirc;n r&uacute;t ngắn c&aacute;ch biệt cho Binh chủng Th&ocirc;ng tin.</p>\r\n\r\n<p><strong>Set 1:&nbsp;</strong>VTV B&igrave;nh Điền Long An 2-1 Binh chủng Th&ocirc;ng tin</p>\r\n\r\n<p>Natalia Lijewska dứt điểm ch&eacute;o g&oacute;c khiến Lưu Thị Ly Ly bất ngờ.</p>\r\n\r\n<p><strong>17h30:&nbsp;</strong>Trận đấu bắt đầu.</p>\r\n\r\n<p><strong>Th&ocirc;ng tin trước trận</strong></p>\r\n\r\n<p>Giai đoạn 1 giải b&oacute;ng chuyền v&ocirc; địch quốc gia 2025 đ&atilde; đi đến ng&agrave;y thi đấu cuối c&ugrave;ng. T&acirc;m điểm sự ch&uacute; &yacute; l&agrave; m&agrave;n so t&agrave;i giữa&nbsp;<a href=\"https://laodong.vn/the-thao/lich-phat-song-bong-chuyen-vtv-binh-dien-long-an-vs-binh-chung-thong-tin-1484486.ldo\" target=\"_self\">VTV B&igrave;nh Điền Long An</a>&nbsp;v&agrave; Binh chủng Th&ocirc;ng tin - TCT Đ&ocirc;ng Bắc khi hai đội c&ugrave;ng c&oacute; 3 chiến thắng, 1 trận thua v&agrave; đang bằng điểm số.</p>\r\n\r\n<p>Với VTV B&igrave;nh Điền Long An, đội đương kim v&ocirc; địch c&oacute; 3 chiến thắng lần lượt trước Th&aacute;i B&igrave;nh, Thanh H&oacute;a, Vietinbank v&agrave; để thua trước LPB Ninh B&igrave;nh. Nh&igrave;n chung, c&aacute;c học tr&ograve; của huấn luyện vi&ecirc;n Th&aacute;i Quang Lai vẫn cho thấy sự đồng đều trong lối chơi cũng như lực lượng.</p>\r\n\r\n<p>Ph&iacute;a b&ecirc;n kia, Binh chủng Th&ocirc;ng tin - TCT đang dần lấy lại được bản sắc dưới thời huấn luyện vi&ecirc;n Phạm Văn Long. Đội b&oacute;ng &aacute;o l&iacute;nh đứng thứ 2 tr&ecirc;n bảng xếp hạng v&agrave; đứng ngay tr&ecirc;n VTV B&igrave;nh Điền Long An. Với sự xuất sắc của những Nguyễn Thị Phương, Phạm Thị Hiền hay Ho&agrave;ng Thị Kiều Trinh, Binh chủng Th&ocirc;ng tin -TCT Đ&ocirc;ng Bắc được k&igrave; vọng sẽ g&acirc;y ra nhiều kh&oacute; khăn cho VTV B&igrave;nh Điền Long An, tạo n&ecirc;n trận cầu giằng co tỉ số hấp dẫn.</p>\r\n', 'hot'),
(291, 126, 'mages2207716702-1743485221-174-7945-6626-1743485254.webp', 'Djokovic góp phần tạo nên \'quái vật\' Mensik', '<p>Ng&ocirc;i sao tuổi teen Jakub Mensik từng ăn tập ở khu huấn luyện của Novak Djokovic, trước khi hạ thần tượng tại chung kết Miami Mở rộng 2025.</p>\r\n\r\n<p><img alt=\"Djokovic (trái) chúc mừng Mensik sau trận chung kết Miami Mở rộng, trên sân Hard Rock, Florida, Mỹ tối 30/3. Ảnh: ATP\" src=\"https://vcdn1-thethao.vnecdn.net/2025/04/01/375292-1743486877-2036-1743486945.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OSbRe_exJ7GsJLddaZGXmg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Djokovic (tr&aacute;i) ch&uacute;c mừng Mensik sau trận chung kết Miami Mở rộng, tr&ecirc;n s&acirc;n Hard Rock, Florida, Mỹ tối 30/3. Ảnh:&nbsp;<em>ATP</em></p>\r\n\r\n<p>Mensik l&agrave; tay vợt tuổi teen thứ tư hạ được Djokovic v&agrave; l&agrave; người hiếm hoi trong lịch sử thắng huyền thoại Serbia&nbsp;<a href=\"https://vnexpress.net/djokovic-thua-chung-ket-miami-mo-rong-2025-4867923.html\">hai loạt tie-break</a>&nbsp;li&ecirc;n tiếp. Lối đ&aacute;nh chắc chắn từ cuối s&acirc;n mang b&oacute;ng d&aacute;ng Djokovic, c&ugrave;ng khả năng giao b&oacute;ng thượng hạng, gi&uacute;p t&agrave;i năng CH Czech trở th&agrave;nh người trẻ thứ hai đăng quang tại Miami Mở rộng, sau Carlos Alcaraz năm 2022.</p>\r\n\r\n<p>&quot;Bạn lu&ocirc;n thấy ch&uacute;t vị đắng trong miệng khi thua chung kết&quot;, Djokovic n&oacute;i trong họp b&aacute;o sau giải Miami Mở rộng h&ocirc;m 30/3. &quot;Nhưng Mensik l&agrave; trường hợp m&agrave; nếu t&ocirc;i thua cậu ấy, t&ocirc;i sẽ thấy vui nhiều hơn buồn. Bởi t&ocirc;i đ&atilde; chứng kiến cậu b&eacute; thi đấu từ khi 15, 16 tuổi v&agrave; rất mong cậu ấy trưởng th&agrave;nh để đoạt c&aacute;c danh hiệu&quot;.</p>\r\n\r\n<p>Djokovic cho biết Mensik đ&atilde; tới CLB của anh ở Belgrade để tập luyện nhiều lần. Chủ nh&acirc;n 24 Grand Slam d&agrave;nh thời gian tr&ecirc;n s&acirc;n tập c&ugrave;ng đ&agrave;n em v&agrave; chỉ bảo th&ecirc;m về kỹ, chiến thuật. Bản th&acirc;n Mensik thần tượng Nole, tiết lộ ch&iacute;nh ng&ocirc;i sao Serbia l&agrave; nguồn cảm hứng để anh theo đuổi quần vợt chuy&ecirc;n nghiệp.</p>\r\n\r\n<p>Khi Mensik ch&agrave;o đời năm 2005, Djokovic đ&atilde; l&agrave; tay vợt trong top 100 thế giới. Ở Miami Mở rộng tuần qua, trong khi Djokovic hướng tới chức v&ocirc; địch ATP Tour thứ 100, th&igrave; Mensik đi t&igrave;m danh hiệu đầu tay. Giữa họ l&agrave; c&aacute;ch biệt 18 tuổi, khoảng c&aacute;ch lớn nhất trong lịch sử chung kết Masters 1000. &quot;T&ocirc;i lu&ocirc;n ngưỡng mộ v&agrave; noi theo tấm gương của Djokovic. Kh&ocirc;ng c&oacute; anh ấy, t&ocirc;i sẽ kh&ocirc;ng ở đ&acirc;y h&ocirc;m nay&quot;, Mensik n&oacute;i khi cầm tr&ecirc;n tay chiếc cup Miami Mở rộng.</p>\r\n\r\n<p>Điều th&uacute; vị l&agrave; cơ hội tập luyện của&nbsp;<a href=\"https://vnexpress.net/jakub-mensik-toi-choi-quan-vot-vi-djokovic-4868012.html\">Mensik</a>&nbsp;tại CLB của Djokovic ở Belgrade do ch&iacute;nh huyền thoại Serbia gợi &yacute;. Sau khi xem Mensik chiến đấu v&agrave; thua trận chung kết đơn nam trẻ Australia Mở rộng 2022, Nole bị ấn tượng mạnh bởi tinh thần của t&agrave;i năng CH Czech. Th&ocirc;ng qua người quản l&yacute;, Djokovic li&ecirc;n hệ với đội ngũ của Mensik để sắp xếp cho đ&agrave;n em tới ăn tập c&ugrave;ng anh tại CLB.</p>\r\n\r\n<p>&quot;Khi ấy t&ocirc;i đ&atilde; tin Mensik c&oacute; thể trở th&agrave;nh tay vợt h&agrave;ng đầu&quot;, Djokovic n&oacute;i. &quot;Cậu b&eacute; c&oacute; tiềm năng, với khả năng chơi to&agrave;n diện khắp mặt s&acirc;n. R&otilde; r&agrave;ng cậu ấy sở hữu c&uacute; giao b&oacute;ng đ&aacute;ng kinh ngạc, gi&uacute;p cậu ấy gi&agrave;nh rất nhiều free-point. H&ocirc;m nay t&ocirc;i l&agrave; nạn nh&acirc;n tiếp theo của cậu ấy, sau khi cậu ấy đ&atilde; to&agrave;n thắng năm loạt tie-break trước đ&oacute;&quot;.</p>\r\n\r\n<p>Djokovic cũng bị ấn tượng bởi c&uacute; tr&aacute;i chắc chắn v&agrave; đủ lực của tay vợt 19 tuổi, nhấn mạnh đ&oacute; l&agrave; đặc sản của c&aacute;c l&ograve; đ&agrave;o tạo ở CH Czech. &quot;Mensik cao 1m93 nhưng bộ ch&acirc;n rất linh hoạt&quot;, anh n&oacute;i. &quot;Cậu ấy c&oacute; thể chạy v&agrave; xoạc ch&acirc;n cứu b&oacute;ng. T&ocirc;i kh&aacute; chắc những kỹ năng đ&oacute; sẽ c&ograve;n được n&acirc;ng cấp&quot;.</p>\r\n\r\n<p>B&aacute;c sĩ trị liệu cũ của Djokovic hiện l&agrave;m việc trong đội Mensik. Giữa họ l&agrave; một sợi d&acirc;y gắn kết với th&ocirc;ng tin được trao đổi qua lại. Sự tiến bộ của Mensik, với danh hiệu ATP đầu ti&ecirc;n v&agrave; vị tr&iacute; 24 thế giới tuần n&agrave;y, v&igrave; thế c&oacute; đ&oacute;ng g&oacute;p kh&ocirc;ng nhỏ từ Nole.</p>\r\n\r\n<p>&quot;Mensik c&ograve;n rất nhiều thời gian để ho&agrave;n thiện&quot;, Djokovic n&oacute;i th&ecirc;m. &quot;Thời gian b&acirc;y giờ kh&ocirc;ng ủng hộ t&ocirc;i. Nhưng t&ocirc;i sẽ kh&ocirc;ng b&agrave;o chữa g&igrave; cả, với hai tay vợt v&agrave;o chung kết, cơ hội l&agrave; như nhau. T&ocirc;i đ&atilde; cố tận dụng tối đa những g&igrave; t&ocirc;i đang c&oacute;. T&ocirc;i đ&atilde; c&oacute; th&ecirc;m một trải nghiệm tuyệt vời ở Miami, đủ để t&ocirc;i tin bản th&acirc;n đ&atilde; đạt được phong độ thực sự tốt&quot;.</p>\r\n\r\n<p><a href=\"https://vnexpress.net/chu-de/novak-djokovic-352\">Djokovic</a>&nbsp;sẽ nghỉ một tuần, rồi dự Monte-Carlo Masters đầu th&aacute;ng 4. Đ&acirc;y l&agrave; sự kiện m&agrave; nếu v&ocirc; địch, tay vợt 37 tuổi sẽ trở th&agrave;nh người duy nhất trong lịch sử thắng mọi Masters 1000 từ ba lần trở l&ecirc;n, v&agrave; c&aacute;n mốc 100 chức v&ocirc; địch ATP.</p>\r\n', 'new'),
(292, 124, 'keely2-1741170220.webp', 'Siêu sao điền kinh tỏa sáng trên thảm đỏ showbiz', '<p>Lễ trao giải &acirc;m nhạc Anh, Brit Awards 2025 h&ocirc;m 1/3 l&agrave; sự kiện mới nhất si&ecirc;u sao chạy 800m người Anh Keely Hodgkinson bừng s&aacute;ng với phong c&aacute;ch thời trang quyến rũ, sang trọng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/05/keely2-1741170220.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PJseEstc00UvlPYCA2C77w\" /></p>\r\n\r\n<p>Hodgkinson thể hiện khả năng kết hợp giữa thể thao v&agrave; thời trang khi xuất hiện trong bộ v&aacute;y hai t&ocirc;ng m&agrave;u đen trắng, t&ocirc;n l&ecirc;n vẻ đẹp h&igrave;nh thể. Sự c&oacute; mặt của nữ VĐV 23 tuổi tại Brit Awards vừa qua thu h&uacute;t sự ch&uacute; &yacute; đặc biệt của CĐV v&agrave; truyền th&ocirc;ng. &quot;Hodgkinson xuất hiện với vẻ ngo&agrave;i quyến rũ, chắc chắn thu h&uacute;t mọi &aacute;nh nh&igrave;n với bộ trang phục t&aacute;o bạo&quot;, tờ&nbsp;<em>Sun&nbsp;</em>(Anh) viết.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/05/New-Project-95-1741170232.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=tRtpoVHi_lHHMMj4nJ9wzg\" /></p>\r\n\r\n<p>Tờ&nbsp;<em>Bild&nbsp;</em>(Đức) th&igrave; khẳng định nữ VĐV điền kinh chiếm &aacute;nh đ&egrave;n s&acirc;n khấu của c&aacute;c sao showbiz v&agrave; c&oacute; một m&agrave;n tr&igrave;nh diễn rất đ&aacute;ng xem. &quot;Hodgkinson đ&atilde; chứng minh rằng c&oacute; thể theo kịp c&aacute;c ng&ocirc;i sao nhạc pop nổi tiếng nhất thế giới khi n&oacute;i về thời trang&quot;, b&aacute;o n&agrave;y viết th&ecirc;m.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/05/New-Project-96-1741170238.jpg?w=1200&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=VY1wbgsbE1k6nBmaEPscQw\" /></p>\r\n\r\n<p>Brit Awards l&agrave; sự kiện thứ hai Hodgkinson tỏa s&aacute;ng tại nh&agrave; h&aacute;t O2 Arena, London, Anh trong chưa đầy nửa th&aacute;ng. H&ocirc;m 20/2, VĐV 23 tuổi cũng c&oacute; mặt để dự buổi ra mắt xe kỷ niệm 75 năm của h&atilde;ng Aston Martin, b&ecirc;n cạnh c&aacute;c tay đua Lewis Hamilton, Lando Norris, đầu bếp Gordon Ramsay v&agrave; diễn vi&ecirc;n Jack Whitehall.</p>\r\n', 'new'),
(293, 120, 'nguyen-thuy-linh-malaysia-mast-1792-5606-1740787961.webp', 'Thùy Linh vào bán kết giải Đức Mở rộng', '<p>Tay vợt nữ số một Việt Nam Nguyễn Th&ugrave;y Linh đang chiếm ưu thế th&igrave; đối thủ người Ấn Độ Tasnim Mir chấn thương v&agrave; bỏ cuộc ở tứ kết giải Đức Mở rộng 2025.</p>\r\n\r\n<p>X&eacute;t về thứ bậc thế giới,&nbsp;<a href=\"https://vnexpress.net/thuy-linh-vao-tu-ket-giai-duc-mo-rong-4854966.html\">Th&ugrave;y Linh</a>&nbsp;nhỉnh hơn. C&ocirc; đang đứng thứ 29, c&ograve;n Tasnim Mir l&agrave; 72. Tuy nhi&ecirc;n, đối thủ người Ấn Độ mới 19 tuổi, &iacute;t hơn tay vợt Việt Nam ch&iacute;n tuổi. Mir cũng từng c&oacute; thời gian d&agrave;i đứng trong top 10 những tay vợt trẻ h&agrave;ng đầu thế giới.</p>\r\n\r\n<p><img alt=\"Thuỳ Linh trong trận đấu với Tasnim Mir tại giải cầu lông Đức mở rộng 2025, rạng sáng 1/3 giờ Hà Nội.\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/01/nguyen-thuy-linh-malaysia-mast-4789-8907-1740787961.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=9bMLgACIynGpsF49SgmMgA\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Th&ugrave;y Linh trong trận đấu với Tasnim Mir tại giải cầu l&ocirc;ng Đức mở rộng 2025, tối 28/2.</p>\r\n\r\n<p>Trước đối thủ trẻ hơn, Th&ugrave;y Linh chọn lối đ&aacute;nh nhanh, đa dạng với c&aacute;ch đường cầu s&acirc;u h&ograve;ng ph&aacute; sức trong set một. Tuy nhi&ecirc;n, Mir kh&aacute;ng cự tốt, nhiều lần h&oacute;a giải những đường cầu kh&oacute; để dẫn trước, trong sự đeo b&aacute;m quyết liệt của tay vợt Việt Nam. Bước ngoặt xảy l&agrave; những t&igrave;nh huống đ&aacute;nh ra ngo&agrave;i v&agrave; kh&ocirc;ng qua lưới của Th&ugrave;y Linh, để Mir thắng 21-19.</p>\r\n\r\n<p>Sang set hai, tay vợt số một Việt Nam buộc phải cẩn trọng hơn. C&ocirc; chuyển sang đ&aacute;nh c&aacute;c đường cầu bền, chỉnh chu trong c&aacute;c pha l&ecirc;n lưới đợi sơ hở của Mir để gi&agrave;nh điểm. Chiến thuật n&agrave;y gi&uacute;p hạt giống số s&aacute;u ph&aacute;t huy t&aacute;c dụng, li&ecirc;n tiếp gi&agrave;nh điểm, tạo c&aacute;ch biệt lớn rồi tiến tới chiến thắng 21-12.</p>\r\n\r\n<p>Đầu set ba, Mir c&oacute; dấu hiệu chấn thương. C&ocirc; chống trả yếu ớt, bị dẫn c&aacute;ch biệt 7-0 trước khi chấp nhận bỏ cuộc.</p>\r\n\r\n<p>Đối thủ của&nbsp;<a href=\"https://vnexpress.net/chu-de/nguyen-thuy-linh-6600\">Th&ugrave;y Linh&nbsp;</a>ở b&aacute;n kết l&agrave; tay vợt Riko Gunji, người Nhật Bản, xếp thứ 41 thế giới. Riko, 22 tuổi, từng v&ocirc; địch đơn nữ giải trẻ thế giới năm 2019.</p>\r\n\r\n<p>Giải cầu l&ocirc;ng Đức Mở rộng nằm trong hệ thống World Tour Super 300 của Li&ecirc;n đo&agrave;n Cầu l&ocirc;ng thế giới (BWF), c&oacute; tổng tiền thưởng l&agrave; 240.000 USD. Tại giải 2024, Th&ugrave;y Linh từng v&agrave;o chung kết v&agrave; thua tay vợt Đan Mạch.</p>\r\n\r\n<p>Năm nay, với việc gi&agrave;nh v&eacute; v&agrave;o b&aacute;n kết, c&ocirc; sẽ gi&agrave;nh 3.480 USD (khoảng 90 triệu) v&agrave; 4.900 điểm thưởng. Nếu v&agrave;o chung kết, số tiền nhận được l&agrave; 9.120 USD c&ugrave;ng 5.950 điểm. C&ograve;n v&ocirc; địch l&agrave; 18.000 USD c&ugrave;ng 7.000 điểm.</p>\r\n\r\n<p>Th&ugrave;y Linh sinh ng&agrave;y 20/11/1997 tại Ph&uacute; Thọ. C&ocirc; thi đấu chuy&ecirc;n nghiệp năm 14 tuổi, v&agrave; 4 năm sau, v&ocirc; địch giải c&aacute;c tay vợt xuất sắc to&agrave;n quốc 2015. Từ 2018, Th&ugrave;y Linh l&agrave; tay vợt nữ số một Việt Nam, với 7 lần li&ecirc;n tiếp v&ocirc; địch giải cầu l&ocirc;ng c&aacute; nh&acirc;n quốc gia. C&ocirc; c&ograve;n 3 lần li&ecirc;n tiếp v&ocirc; địch Vietam Open từ 2022. Th&ugrave;y Linh cũng từng dự Olympic Tokyo 2020 v&agrave; Paris 2024.</p>\r\n', 'new'),
(294, 122, 'ccibngnhnclunimgii-1695681529-8847-9772-1695681556.webp', ' Thể thaoCác môn khácThứ ba, 26/9/2023, 10:00 (GMT+7) Giải vô địch bóng chày Hà Nội lần đầu được tổ chức', '<p>Giải đấu Hanoi Baseball Major Series d&agrave;nh cho c&aacute;c CLB b&oacute;ng ch&agrave;y tại H&agrave; Nội khởi tranh h&ocirc;m 24/9 tại s&acirc;n Dragon Rugby Field.</p>\r\n\r\n<p>Sau th&agrave;nh c&ocirc;ng của hai giải to&agrave;n quốc c&aacute;c năm 2022 v&agrave; 2023, b&oacute;ng ch&agrave;y Việt Nam tiếp tục s&ocirc;i động với giải đấu đầu ti&ecirc;n mang m&ocirc; h&igrave;nh địa phương. S&aacute;u CLB b&oacute;ng ch&agrave;y tr&ecirc;n địa b&agrave;n H&agrave; Nội với hơn 200 VĐV Việt Nam v&agrave; quốc tế sẽ tranh t&agrave;i trong năm tuần, để t&igrave;m ra nh&agrave; v&ocirc; địch của kỳ giải đầu ti&ecirc;n tại Thủ đ&ocirc;.</p>\r\n\r\n<p>Từ 24/9 đến 8/10, c&aacute;c trận đấu v&ograve;ng bảng diễn ra v&agrave;o s&aacute;ng Chủ nhật h&agrave;ng tuần tr&ecirc;n s&acirc;n Hanoi Dragon Rugby Field, T&acirc;n Mỹ. Bảng A gồm c&aacute;c đội Hanoi Archers, Devil Bats, Hanoi Phantoms c&ograve;n bảng B gồm Hanoi 29ers, HRO v&agrave; Alligator. C&aacute;c đội thi đấu theo thể thức v&ograve;ng tr&ograve;n một lượt, chọn ra hai đội nhất nh&igrave; mỗi bảng v&agrave;o đấu b&aacute;n kết s&aacute;ng 15/10. Trận chung kết v&agrave; lễ bế mạc diễn ra ng&agrave;y 22/10.</p>\r\n\r\n<p><img alt=\"Đại diện ban tổ chức cùng sáu đội bóng tại lễ khai bạc giải bóng chày Hà Nội ở sân Dragon Rugby Field, Tân Mỹ. Ảnh: HBMS\" src=\"https://vcdn1-thethao.vnecdn.net/2023/09/26/cac-doi-bong-nhan-co-luu-niem-7334-9165-1695681556.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=PnIEtlxvlEjPS3uYNT0-fw\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Đại diện ban tổ chức c&ugrave;ng s&aacute;u đội b&oacute;ng tại lễ khai bạc giải b&oacute;ng ch&agrave;y H&agrave; Nội ở s&acirc;n Dragon Rugby Field, T&acirc;n Mỹ. Ảnh:&nbsp;<em>HBMS</em></p>\r\n\r\n<p>&quot;Tại nơi được coi l&agrave; c&aacute;i n&ocirc;i của b&oacute;ng ch&agrave;y tại Việt Nam, ch&uacute;ng t&ocirc;i tự h&agrave;o tổ chức giải b&oacute;ng ch&agrave;y kh&ocirc;ng chuy&ecirc;n đầu ti&ecirc;n tại thủ đ&ocirc; H&agrave; Nội&quot;, &ocirc;ng T&ocirc; Duy, trưởng ban tổ chức giải chia sẻ trong lễ khai mạc. &quot;B&oacute;ng ch&agrave;y đang dần trở th&agrave;nh m&ocirc;n thể thao quen thuộc với người Việt Nam. Giải đấu diễn ra với mục đ&iacute;ch x&acirc;y dựng phong tr&agrave;o tập luyện m&ocirc;n thể thao n&agrave;y v&agrave; ph&aacute;t triển cộng đồng y&ecirc;u th&iacute;ch b&oacute;ng ch&agrave;y tại H&agrave; Nội&quot;.</p>\r\n\r\n<p>Kể từ khi Li&ecirc;n đo&agrave;n B&oacute;ng ch&agrave;y v&agrave; B&oacute;ng mềm Việt Nam được th&agrave;nh lập năm 2021, Hanoi Baseball Major Series được xem như giải lớn thứ ba, sau hai giải b&oacute;ng ch&agrave;y to&agrave;n quốc lần lượt được tổ chức tại TP HCM v&agrave; H&agrave; Nội c&aacute;c năm 2022 v&agrave; 2023. Sau khi giải v&ocirc; địch b&oacute;ng ch&agrave;y H&agrave; Nội kết th&uacute;c, c&aacute;c giải đấu ở miền Trung v&agrave; miền Nam dự kiến sẽ được tổ chức để chọn ra c&aacute;c đội tuyển mạnh nhất g&oacute;p mặt ở giải tổng v&agrave;o cuối m&ugrave;a.</p>\r\n\r\n<p>Giải v&ocirc; địch b&oacute;ng ch&agrave;y H&agrave; Nội ra đời dựa tr&ecirc;n t&acirc;m huyết của nhiều tổ chức v&agrave; c&aacute; nh&acirc;n, với mong muốn quảng b&aacute; h&igrave;nh ảnh bộ m&ocirc;n n&agrave;y đến gần hơn với người h&acirc;m mộ, v&agrave; n&acirc;ng cao tr&igrave;nh độ chuy&ecirc;n m&ocirc;n của đội h&igrave;nh VĐV b&oacute;ng ch&agrave;y tr&ecirc;n địa b&agrave;n thủ đ&ocirc;. &quot;Ch&uacute;ng t&ocirc;i mong rằng giải đấu sẽ l&agrave; c&uacute; h&iacute;ch cho m&ocirc;n b&oacute;ng ch&agrave;y tại H&agrave; Nội n&oacute;i ri&ecirc;ng v&agrave; Việt Nam n&oacute;i chung, để trong tương lai người d&acirc;n sẽ c&oacute; th&ecirc;m một loại h&igrave;nh giải tr&iacute;, thể dục thể thao l&agrave;nh mạnh mỗi cuối tuần&quot;, &ocirc;ng T&ocirc; Duy n&oacute;i th&ecirc;m.</p>\r\n\r\n<p>Ở Đ&ocirc;ng Nam &Aacute;, b&oacute;ng ch&agrave;y Việt Nam đang được xem l&agrave; &quot;v&ugrave;ng trũng&quot;, với tr&igrave;nh độ chuy&ecirc;n m&ocirc;n c&ograve;n k&eacute;m c&aacute;c quốc gia trong khu vực. Những giải đấu lớn được tổ chức với tần suất ng&agrave;y một lớn hứa hẹn th&uacute;c đẩy sự ph&aacute;t triển của b&oacute;ng ch&agrave;y tại Việt Nam, gi&uacute;p t&igrave;m ra những &quot;vi&ecirc;n ngọc th&ocirc;&quot;. Th&ocirc;ng qua c&aacute;c giải đấu, c&aacute;c VĐV xuất sắc cũng sẽ được tuyển chọn v&agrave;o đội tuyển quốc gia, để học hỏi, cọ x&aacute;t, cố gắng c&oacute; th&agrave;nh t&iacute;ch ở SEA Games 33 năm 2025.</p>\r\n\r\n<p>B&oacute;ng ch&agrave;y đều đặn g&oacute;p mặt trong c&aacute;c kỳ Thế vận hội từ khi ch&iacute;nh thức trở th&agrave;nh một bộ m&ocirc;n thi đấu Olympic năm 1992. Ở Asiad 19 đang diễn ra tại Trung Quốc, Việt Nam kh&ocirc;ng cử đội tuyển tham gia tranh t&agrave;i ở m&ocirc;n thể thao n&agrave;y.</p>\r\n\r\n<p>B&oacute;ng ch&agrave;y c&oacute; nguồn gốc từ c&aacute;c nước ch&acirc;u Mỹ v&agrave; ph&aacute;t triển mạnh mẽ ở H&agrave;n Quốc, Nhật Bản, nhưng ở Đ&ocirc;ng Nam &Aacute; lại &iacute;t phổ biến. Từ thập ni&ecirc;n những năm 90, b&oacute;ng ch&agrave;y bắt đầu du nhập v&agrave;o Việt Nam th&ocirc;ng qua quan hệ hợp t&aacute;c của c&aacute;c tập đo&agrave;n kinh tế, ngoại giao v&agrave; một số tổ chức quốc tế.</p>\r\n\r\n<p>Hiện nay, phong tr&agrave;o tập luyện v&agrave; thi đấu b&oacute;ng ch&agrave;y ở Việt Nam đ&atilde; v&agrave; đang thu h&uacute;t kh&aacute; đ&ocirc;ng người tham gia, đặc biệt ở H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh. C&aacute;c li&ecirc;n đo&agrave;n non trẻ hầu hết hoạt động từ nguồn x&atilde; hội h&oacute;a.</p>\r\n', 'new'),
(295, 121, 'viet-nam-trung-quoc-giai-bong-5486-6277-1743323110.webp', 'VĐV bóng rổ Việt Nam khóc trong trận thua ngược Trung Quốc', '<p>Trương Thảo My bật kh&oacute;c v&igrave; chấn thương l&agrave;m lỡ phần c&ograve;n lại của trận đấu, khi Việt Nam thua ngược Trung Quốc ở tứ kết giải b&oacute;ng rổ 3x3 ch&acirc;u &Aacute; 2025.</p>\r\n\r\n<p>Ở 7 ph&uacute;t 25 gi&acirc;y, Việt Nam dẫn Trung Quốc 13-12. Thảo My di chuyển từ ngo&agrave;i v&ograve;ng cung v&agrave;o trong, nhưng trượt ng&atilde; dẫn đến chấn thương ở cổ ch&acirc;n phải. Theo luật, b&oacute;ng phải ra ngo&agrave;i s&acirc;n th&igrave; c&aacute;c đội mới c&oacute; thể thay người. Thảo My tiếp tục đứng dậy thi đấu với những bước ch&acirc;n tập tễnh gần 30 gi&acirc;y. Trong thời gian n&agrave;y, Trung Quốc n&eacute;m th&agrave;nh c&ocirc;ng một quả hai điểm dể dẫn ngược.</p>\r\n\r\n<p>Sau khi về khu kỹ thuật, Thảo My một tay &ocirc;m cổ ch&acirc;n, một tay th&aacute;o d&acirc;y gi&agrave;y v&agrave; kh&oacute;c nức nở. Trong khi được chăm s&oacute;c y tế, c&ocirc; li&ecirc;n tục &ocirc;m đầu, c&oacute; l&uacute;c lấy &aacute;o che mặt.</p>\r\n\r\n<p><img alt=\"Trương Thảo My khóc ở khu kỹ thuật sau khi gặp chấn thương cổ chân, ở trận Việt Nam thua Trung Quốc 15-17, tại tứ kết nội dung nữ giải bóng rổ 3x3 châu Á 2025, ở Singapore ngày 30/3. Ảnh: FIBA\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/30/viet-nam-trung-quoc-giai-bong-1514-2488-1743323109.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=g6LZJDiOMcUkTXUcYC4Wxw\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Trương Thảo My kh&oacute;c ở khu kỹ thuật sau khi gặp chấn thương ở trận Việt Nam thua Trung Quốc 15-17, tại tứ kết nội dung nữ giải b&oacute;ng rổ 3x3 ch&acirc;u &Aacute; 2025, ở Singapore ng&agrave;y 30/3. Ảnh: FIBA</p>\r\n\r\n<p><img alt=\"Trương Thảo My (số 11) lấy áo che mặt trong khi các đồng đội dừng nghỉ ở cuối trận Việt Nam thua Trung Quốc. Ảnh: FIBA\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/30/viet-nam-trung-quoc-giai-bong-6496-6559-1743323109.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=HrvpSCHVk8uAshr_TWXzDw\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Trương Thảo My (số 11) lấy &aacute;o che mặt trong khi c&aacute;c đồng đội dừng nghỉ ở cuối trận Việt Nam thua Trung Quốc. Ảnh:&nbsp;<em>FIBA</em></p>\r\n\r\n<p>Vắng Thảo My, Việt Nam thiếu đi nguồn sức mạnh quan trọng, đ&aacute;nh mất lợi thế trước hạt giống số một Trung Quốc, để thua ngược 15-17. Trong 30 gi&acirc;y cuối, đội tuyển c&oacute; hai cơ hội n&eacute;m hai thuộc về Trương Thảo Vy, nhưng kh&ocirc;ng th&agrave;nh c&ocirc;ng trước khả năng &aacute;p s&aacute;t của đối thủ.</p>\r\n\r\n<p>Trước trận đấu tại Khu li&ecirc;n hợp thể thao Quốc gia Singapore chiều nay, Việt Nam bị đ&aacute;nh gi&aacute; thấp hơn. Bốn c&ocirc; g&aacute;i Trương Thảo My (1,75 m), Trương Thảo Vy (1,73 m), Nguyễn Thị Tiểu Duy (1,74 m) v&agrave; B&ugrave;i Thu Hằng (1,73 m) chạm tr&aacute;n Hu Duoling (1,77 m), Li Wenxia (1,82 m), Li Yuyan (1,85 m) v&agrave; Zhou Mengyun (1,85 m) vượt trội về h&igrave;nh thể.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Việt Nam bất ngờ dẫn điểm, c&oacute; l&uacute;c l&agrave; 9-5 trước khi đến 13-12. C&aacute;c c&ocirc; g&aacute;i &aacute;o đỏ k&egrave;m người v&agrave; chạy b&agrave;i tốt. Thảo My th&agrave;nh c&ocirc;ng hai trong bốn t&igrave;nh huống n&eacute;m ngo&agrave;i v&ograve;ng cung, c&ograve;n người em song sinh Thảo Vy ghi 11 điểm với nhiều pha đột ph&aacute; đ&aacute;nh cận rổ tốt. Thế trận chỉ đảo chiều khi Thảo My chấn thương.</p>\r\n', 'hot');
INSERT INTO `posts` (`id`, `categoryId`, `image`, `productName`, `details`, `status`) VALUES
(296, 121, 'viet-nam-bong-ro-3x3-chau-a-20-8305-6431-1743070741.webp', 'Hạ Thái Lan, Việt Nam lách qua cửa hẹp ở giải bóng rổ châu Á', '<p>Việt Nam hạ Th&aacute;i Lan 21-15 ở lượt cuối v&ograve;ng sơ loại, để v&agrave;o v&ograve;ng ch&iacute;nh nội dung nam giải b&oacute;ng rổ 3x3 ch&acirc;u &Aacute; 2025.</p>\r\n\r\n<p>H&ocirc;m qua 26/3, Việt Nam thắng New Caledonia (hạt giống số 21) với tỷ số 21-11, nhưng thua 16-17 trước đối thủ cạnh tranh trực tiếp l&agrave; Turkmenistan (13). Trong khi đ&oacute;, Th&aacute;i Lan (12) lần lượt hạ New Caledonia 21-13 v&agrave; Turkmenistan 21-15.</p>\r\n\r\n<p>Th&aacute;i Lan nhờ đ&oacute; dẫn đầu bảng D v&ograve;ng sơ loại, c&ograve;n Việt Nam xếp thứ ba, c&oacute; c&ugrave;ng một trận thắng như Turkmenistan nhưng k&eacute;m hiệu số đối đầu. Trước trận quyết định h&ocirc;m nay, đội tuyển kh&ocirc;ng chỉ t&iacute;nh to&aacute;n về đối thủ m&agrave; c&ograve;n phải xem lại c&aacute;c quy định. Thứ bậc v&ograve;ng bảng lần lượt được x&aacute;c định dựa v&agrave;o số trận thắng, chỉ số đối đầu v&agrave; số điểm ghi được.</p>\r\n\r\n<p><img alt=\"Trần Đăng Khoa và Justin Young (số 12) mừng chiến thắng 21-15 của Việt Nam trước Thái Lan ở lượt cuối bảng D vòng sơ loại giải bóng rổ 3x3 châu Á 2025, tại Singapore vào chiều 27/3. Ảnh: FIBA\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/27/viet-nam-bong-ro-3x3-chau-a-20-7073-4632-1743070741.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=E01ROdLJxhmtzoUuppNMHg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Trần Đăng Khoa v&agrave; Justin Young (số 12) mừng chiến thắng 21-15 của Việt Nam trước Th&aacute;i Lan ở lượt cuối bảng D v&ograve;ng sơ loại giải b&oacute;ng rổ 3x3 ch&acirc;u &Aacute; 2025, tại Singapore v&agrave;o chiều 27/3. Ảnh:&nbsp;<em>FIBA</em></p>\r\n\r\n<p>Kịch bản đầu ti&ecirc;n được đưa ra l&agrave; Turkmenistan thua New Caledonia, khi ấy Việt Nam chỉ cần thắng Th&aacute;i Lan. Cuối c&ugrave;ng, Turkmenistan thắng 17-14. V&igrave; vậy, đội tuyển phải &aacute;p dụng kịch bản thứ hai l&agrave; thắng Th&aacute;i Lan, ghi 21 điểm v&agrave; kh&ocirc;ng để đối thủ ghi qu&aacute; 15 điểm. Khi ấy, chỉ số đối đầu giữa Việt Nam, Th&aacute;i Lan v&agrave; Turkmenistan c&ugrave;ng c&oacute; một thắng v&agrave; một thua, nhưng đội tuyển sẽ dẫn đầu nhờ ghi được 37 điểm, xếp sau l&agrave; Th&aacute;i Lan (36) v&agrave; Turkmenistan (32).</p>\r\n\r\n<p>Trận đấu diễn ra tại s&acirc;n ngo&agrave;i trời Khu li&ecirc;n hợp Thể thao Quốc gia Singapore. Việt Nam gồm Justin Young (t&ecirc;n tiếng Việt l&agrave; Dương Vĩnh Lu&acirc;n, cao 1,93 m), Trần Đăng Khoa (1,83 m), Đinh Thanh T&acirc;m (1,92 m) v&agrave; Chris Dieker (Đặng Qu&yacute; Kiệt, 2,01 m). Cả bốn đều l&agrave; Việt kiều trưởng th&agrave;nh từ b&oacute;ng rổ Mỹ, trong đ&oacute; Young gốc Đ&agrave;i Loan, c&ograve;n ba đồng đội gốc Mỹ. Bốn VĐV c&oacute; cơ hội về Việt Nam thi đấu khi giải b&oacute;ng rổ Việt Nam (VBA) được th&agrave;nh lập năm 2016.</p>\r\n\r\n<p>Trong khi đ&oacute;, Th&aacute;i Lan c&oacute; Emmanuel Ejesu (2,04 m), Nicola Franco (1,94 m), Chanatip Jakrawan (2,04 m) v&agrave; Frederick Lish (1,78 m).</p>\r\n', 'new'),
(297, 123, 'u17-viet-nam-2025-2-jpg-174348-8435-6941-1743486317.webp', 'Việt Nam thuộc nhóm thấp nhất U17 châu Á 2025', '<p>Chiều cao trung b&igrave;nh của Việt Nam xếp thứ 13, c&ograve;n c&acirc;n nặng trung b&igrave;nh đứng thứ 10 ở v&ograve;ng chung kết U17 ch&acirc;u &Aacute; 2025.</p>\r\n\r\n<p>C&aacute;c đội tuyển dự giải được đăng k&yacute; tối thiểu 18 v&agrave; tối đa 23 cầu thủ, trong đ&oacute; bắt buộc c&oacute; ba thủ m&ocirc;n. Cầu thủ phải sinh từ ng&agrave;y 1/1/2008 đến 31/12/2010. Danh s&aacute;ch đầu ti&ecirc;n được chốt v&agrave;o ng&agrave;y 29/3, v&agrave; chốt lần cuối một ng&agrave;y trước trận ra qu&acirc;n.</p>\r\n\r\n<p>HLV Cristiano Roland tạm gạch t&ecirc;n ba tiền vệ l&agrave; Trần Ho&agrave;ng Khanh, H&agrave; Huy Ph&uacute;c v&agrave; Mai Viết Dũng. Đội đăng k&yacute; đủ 23 cầu thủ với chiều cao trung b&igrave;nh 1,748 m &ndash; xếp thứ 13 trong 16 đội dự giải, c&ograve;n c&acirc;n nặng xếp thứ 10 với 68,3 kg.</p>\r\n\r\n<p><img alt=\"Thủ Môn Nguyễn Văn Thăng Long (số 28) và Hoa Xuân Tín (phải) là hai cầu thủ cao nhất U17 Việt Nam dự vòng chung kết U17 châu Á 2025 tại Arab Saudi. Ảnh: Hiếu Lương\" src=\"https://vcdn1-thethao.vnecdn.net/2025/04/01/u17-viet-nam-2025-1-jpg-174348-9594-7621-1743486317.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XShG6rEd0zCBFviHNOHeDA\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>Thủ M&ocirc;n Nguyễn Văn Thăng Long (số 28) v&agrave; Hoa Xu&acirc;n T&iacute;n (phải) l&agrave; hai cầu thủ cao nhất U17 Việt Nam dự v&ograve;ng chung kết U17 ch&acirc;u &Aacute; 2025 tại Arab Saudi. Ảnh: Hiếu Lương</p>\r\n\r\n<p>Việt Nam c&oacute; s&aacute;u cầu thủ cao tr&ecirc;n 1,8 m l&agrave; thủ m&ocirc;n Hoa Xu&acirc;n T&iacute;n (1,87), Nguyễn Văn Thăng Long (1,85), Trần Văn Đạt (1,8), hậu vệ L&ecirc; Quang Trường (1,83), tiền đạo Trần Gia Bảo (1,8) v&agrave; Nguyễn Thi&ecirc;n Ph&uacute; (1,82). Cầu thủ thấp nhất l&agrave; tiền vệ Đặng C&ocirc;ng Anh Kiệt với 1,62 m. Hai cầu thủ thấp dưới 1,7 m c&ograve;n lại l&agrave; Nguyễn Đức Nhật (1,64) v&agrave; Nguyễn văn Qu&acirc;n (1,69).</p>\r\n\r\n<p>Chiều cao trung b&igrave;nh của Việt Nam thuộc bốn đội cao dưới 1,75 m, trong đ&oacute; xếp tr&ecirc;n Oman (1,737), Indonesia (1,731) v&agrave; Yemen (1,724). Top 5 đội dẫn đầu l&agrave; Iran (1,822), H&agrave;n Quốc (1,806), Uzbekistan (1,801), Australia (1,795) v&agrave; Trung Quốc (1,792).</p>\r\n\r\n<p>C&acirc;n nặng trung b&igrave;nh của Việt Nam thuộc 11 đội dưới 70 kg, trong đ&oacute; xếp tr&ecirc;n UAE (67,2), Triều Ti&ecirc;n (66,1), Oman (64,4), Indonesia (62,4), Afghanistan (62) v&agrave; Yemen (58). Top 5 đội dẫn đầu l&agrave; H&agrave;n Quốc (74,7), Iran (72,7), Australia (71,7), Uzbekistan (71,1) v&agrave; Trung Quốc (70,9).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chiều cao v&agrave; c&acirc;n nặng trung b&igrave;nh kỳ n&agrave;y vẫn phản &aacute;nh sự ch&ecirc;nh lệch thể chất thường như thường lệ, khi c&aacute;c đội tuyển Đ&ocirc;ng Nam &Aacute; hoặc c&aacute;c nước bất ổn ch&iacute;nh trị thuộc nh&oacute;m thấp nhất. Tuy nhi&ecirc;n, Th&aacute;i Lan c&oacute; sự cải thiện khi tiệm cận nh&oacute;m giữa.</p>\r\n\r\n<p>Th&aacute;i Lan đứng thứ ch&iacute;n về chiều cao (1,768 m) thứ t&aacute;m về c&acirc;n nặng (69,7 kg). Đội c&oacute; ch&iacute;n cầu thủ cao tr&ecirc;n 1,8 m, trong đ&oacute; thủ m&ocirc;n Poomraphee Siribunyakul cao 1,91 m v&agrave; đang thi đấu cho THPT Kashima Gakuen ở Nhật Bản. Tiền đạo đang ăn tập tại Man Utd l&agrave; Tyler Mexes cũng cao 1,82 m.</p>\r\n', 'hot'),
(298, 123, 'newproject20250331t085137402-1-3304-5781-1743386084.webp', 'Barca có 20 trận ghi bốn bàn', '<p>Thầy tr&ograve; HLV Hansi Flick tr&uacute;t mưa b&agrave;n tr&ecirc;n mọi đấu trường từ đầu m&ugrave;a 2024-2025.</p>\r\n\r\n<p><a href=\"https://vnexpress.net/the-thao/du-lieu-bong-da/tran-dau/1208747/barca-girona\">Chi tiết trận đấu</a></p>\r\n\r\n<p>Tối 30/3, Barca đ&egrave; bẹp Girona 4-1, đ&aacute;nh dấu trận thứ 20 m&agrave; họ ghi &iacute;t nhất bốn b&agrave;n từ đầu m&ugrave;a. Họ l&agrave;m được điều n&agrave;y trong 11 trận La Liga, bốn trận Cup Nh&agrave; vua, một trận Si&ecirc;u Cup T&acirc;y Ban Nha v&agrave; bốn trận Champions League.</p>\r\n\r\n<p>Danh s&aacute;ch nạn nh&acirc;n của Barca c&oacute; cả Real, Atletico, Bayern, Villarreal, Sevilla, Betis v&agrave; Benfica. Trong đ&oacute;, c&oacute; đến năm đội bị &quot;ăn h&agrave;nh&quot; hai trận gồm Real (thua 0-4 v&agrave; 2-5), Atletico (2-4 v&agrave; 4-4), Girona (c&ugrave;ng 1-4), Valencia (1-7 v&agrave; 0-5), Sevilla (1-5 v&agrave; 1-4).</p>\r\n\r\n<p><img alt=\"Các cầu thủ Barca mừng bàn trong trận thắng Girona 4-1 ở vòng 29 La Liga tối 30/3. Ảnh: Reuters\" src=\"https://vcdn1-thethao.vnecdn.net/2025/03/31/New-Project-2025-03-31T085137-7974-4871-1743386084.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=m_C4D_1bAJkB67EY_FRAHw\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p>C&aacute;c cầu thủ Barca ăn mừng trong trận thắng Girona 4-1 ở v&ograve;ng 29 La Liga tối 30/3. Ảnh:&nbsp;<em>Reuters</em></p>\r\n\r\n<p>Barca đ&atilde; ghi tổng cộng 139 b&agrave;n trong 45 trận tr&ecirc;n mọi đấu trường từ đầu m&ugrave;a, đạt tỷ lệ trung b&igrave;nh hơn ba b&agrave;n mỗi trận. Họ cũng bất bại 20 trận từ đầu năm 2025.</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i lu&ocirc;n muốn tấn c&ocirc;ng v&agrave; tạo cơ hội&quot;, Flick n&oacute;i sau trận thắng Girona tối 30/3. &quot;Khi phản c&ocirc;ng, kh&ocirc;ng chỉ c&aacute;c tiền đạo, m&agrave; ngay cả c&aacute;c tiền vệ cũng d&acirc;ng l&ecirc;n. Nếu c&oacute; cơ hội, ch&uacute;ng t&ocirc;i lu&ocirc;n cố ghi b&agrave;n. Đ&oacute; l&agrave; điều t&ocirc;i thực sự y&ecirc;u th&iacute;ch ở đội b&oacute;ng n&agrave;y&quot;.</p>\r\n\r\n<p>Với c&uacute; đ&uacute;p v&agrave;o lưới Girona, Robert Lewandowski n&acirc;ng số lần lập c&ocirc;ng từ đầu m&ugrave;a l&ecirc;n 38 b&agrave;n. Tiền đạo 36 tuổi đ&atilde; vượt qua th&agrave;nh t&iacute;ch hai m&ugrave;a trước l&agrave; 33 v&agrave; 26 b&agrave;n. Anh cũng tiếp tục dẫn đầu cuộc đua Vua ph&aacute; lưới&nbsp;<a href=\"https://vnexpress.net/chu-de/la-liga-41\">La Liga</a>&nbsp;với 25 b&agrave;n, hơn người xếp thứ hai Kylian Mbappe ba b&agrave;n.</p>\r\n\r\n<p>&quot;Điều n&agrave;y thật tốt v&agrave; đ&oacute; l&agrave; l&yacute; do tại sao ch&uacute;ng t&ocirc;i cần cậu ấy&quot;, Flick n&oacute;i về Lewandowski. &quot;T&ocirc;i biết cậu ấy muốn trở th&agrave;nh Vua ph&aacute; lưới, nhưng như t&ocirc;i vẫn n&oacute;i, điều quan trọng l&agrave; đội b&oacute;ng. Khi ch&uacute;ng t&ocirc;i chiến thắng v&agrave; ghi b&agrave;n, c&oacute; lẽ cơ hội để cậu ấy l&ecirc;n ng&ocirc;i sẽ cao hơn&quot;.</p>\r\n\r\n<p><a href=\"https://vnexpress.net/chu-de/barcelona-68\">Barca</a>&nbsp;hiện dẫn đầu La Liga với 66 điểm sau 29 trận, hơn Real ba điểm. Trong ch&iacute;n trận c&ograve;n lại, họ c&oacute; năm trận s&acirc;n nh&agrave;, gồm cả trận tiếp Real ở v&ograve;ng 35 ng&agrave;y 11/5. Cuộc đại chiến mang t&ecirc;n El Clasico n&agrave;y nhiều khả năng quyết định chức v&ocirc; địch.</p>\r\n', 'none');

